def get_text(key: str, language: str = DEFAULT_LANGUAGE, **kwargs: Any) -> str:
    try:
        text = TRANSLATIONS[language].get(key)
        if text is None:
            raise KeyError(f"–ö–ª—é—á –ø–µ—Ä–µ–≤–æ–¥–∞ '{key}' –Ω–µ –Ω–∞–π–¥–µ–Ω")
        return text.format(**kwargs) if kwargs else text
    except Exception as e:
        print(f"–û—à–∏–±–∫–∞ –ø—Ä–∏ –ø–æ–ª—É—á–µ–Ω–∏–∏ —Ç–µ–∫—Å—Ç–∞: {e}")
        return f"–û—à–∏–±–∫–∞ –ø–µ—Ä–µ–≤–æ–¥–∞: {key}"

